import React, { useContext } from "react";
import { MdClose } from "react-icons/md";
import { BsCartX } from "react-icons/bs";
import { Context } from "../../utils/context";
import CartItem from "./CartItem/CartItem";
import Form from "./Form";
import "./Cart.scss";
import { message } from "antd";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const { cartItems, setShowCart, cartSubTotal } = useContext(Context);
  const navigate = useNavigate();

  const getSum = (items, key) =>
    items.reduce((sum, item) => sum + item.attributes[key], 0);

  const saveOrder = {
    mutateAsync: async (order) => {
      // Implement the saveOrder logic here
    },
  };

  const removeCartItem = {
    mutateAsync: async (id) => {
      // Implement the removeCartItem logic here
    },
  };

  const errorhandler = (error) => error.message;

  const handleCheckout = async (e) => {
    e.preventDefault();

    const amount = getSum(cartItems, "price") * 100;
    const intAmount = Math.round(amount);
    const amountDec = amount.toFixed(2).toString();

    const createOrder = {
      mutateAsync: async (order) => {
        return {
          amount: "amount",
          id: order,
        };
      },
    };

    try {
      const orderres = await createOrder.mutateAsync({
        amount: intAmount,
        currency: "INR",
        cartItems,
        receipt: `receipt#${Date.now()}`,
        notes: {
          address: "User address",
        },
        description: "Purchase Description",
        products: cartItems,
      });

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: intAmount,
        currency: "INR",
        name: "Living By Aad",
        description: "Purchase Description",
        image:
          "data:image/jpeg;base64,",
        order_id: orderres?.id,
        handler: async (res) => {
          try {
            const order = {
              total: amountDec,
              carts: cartItems.map((el) => el.id),
              // products: Res.products_id,
              razorpayPaymentId: res.razorpay_payment_id,
              razorpaySignature: res.razorpay_signature,
              razorpayOrderId: res.razorpay_order_id,
            };
            await saveOrder.mutateAsync({ data: order });
            message.success("Order placed successfully");

            // Remove cart items
            for (const item of cartItems) {
              await removeCartItem.mutateAsync(item.id);
            }
            setShowCart(false);
            navigate("/", { replace: true });
          } catch (error) {
            console.error(errorhandler(error));
            message.error("Order failed");
          }
        },
        prefill: {
          name: "Customer Name",
          email: "customer@example.com",
          contact: "9999999999",
        },
        notes: {
          titles: cartItems.map((obj) => obj?.attributes?.title).join("|"),
        },
        description: "Purchase Description1",
        theme: {
          color: "#3399cc",
        },
        method: {
          upi: true,
          card: true,
          netbanking: true,
          wallet: true,
        },
      };

      const rzpay = new window.Razorpay(options);
      rzpay.open();

      rzpay.on("payment.failed", (response) => {
        console.log("payment failed", response);
        message.error("Payment failed");
      });
    } catch (error) {
      console.error(
        "Error in handleCheckout:",
        error.response ? error.response.data : error.message
      );
      message.error("Checkout failed");
    }
  };

  return (
    <div className="cart-container">
      <Form />
      <div className="cart-panel">
        <div className="opac-layer" onClick={() => setShowCart(false)}></div>
        <div className="cart-content">
          <div className="cart-header">
            <span className="heading">Shopping Cart</span>
            <span className="close-btn" onClick={() => setShowCart(false)}>
              <MdClose className="close-btn" />
              <span className="text">close</span>
            </span>
          </div>

          {!cartItems.length ? (
            <div className="empty-cart">
              <BsCartX />
              <span>No products in the cart.</span>
              <button className="return-cta" onClick={() => navigate("/")}>
                RETURN TO SHOP
              </button>
            </div>
          ) : (
            <>
              <CartItem />
              <div className="cart-footer">
                <div className="subtotal">
                  <span className="text">Subtotal:</span>
                  <span className="text total">&#8377;{cartSubTotal}</span>
                </div>
                <div className="button">
                  <button className="checkout-cta" onClick={handleCheckout}>
                    Checkout
                  </button>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cart;
